sourcesContent:
import _User from "../../Models/User"
import { GenerateOTP } from "../../Generic services/generateOTP";
import { duplicateDTO, save } from "../../utilitiy/DB_Executer";
import { accessToken } from "../../utilitiy/token";
import jwt from 'jsonwebtoken';
import { signUpValidator } from "./User.validator"
import bcrypt from 'bcrypt'
import { v4 as uuid } from 'uuid';
import config from "../../config";
import { user } from "../../utilitiy/Fn.excute";
import _Otp from "../../Models/useOTPVerificaition";
import { sendOTPVerfication, verifyOTPservice } from "../OTPverification.js/OTP.service";
const TOKENKEY = 'tokenkey';
const SECRET_KEY = config.SECRET_KEY;

// const salt = bcrypt.genSaltSync(10);

export const signUpService = async (data) => {
    const userDTO = await signUpValidator.validate({ ...data });
    const isUserExist = _User.findOne({ phoneNumber: data.phoneNumber });
    // check for duplicate phone Numbers
    console.log(isUserExist.phoneNumber == userDTO.phoneNumber)
    if (isUserExist == true) {
        return {
            "title": "Duplicate user",
            "messsage": `user already signed up `
        }
    }

    const password_Len = {
        password: userDTO.password
    }
    let userPassword;
    userPassword = (Object.values(password_Len))[0]

    // const _userpassword = user(userPassword)(10);
    if (!userPassword) {
        return {
            title: 'Hashing failed',
            message: "Error occured while hashing user password"
        }
    }
    // TODO: hash the password using bcyrpt
    const _userpassword = await bcrypt.hash(userPassword, 10)
    // create a payload for the token ;
    const payloadDTO =
    {
        id: uuid(),
        email: userDTO.email,
        role: userDTO.role
    }

    // TODO :  generate access token and save to he database
    const token = accessToken(payloadDTO, SECRET_KEY);
    if (!token) {
        return {
            auth: false,
            title: `token not found`,
            message: `Invalid token ${token}`
        }
    }

    const unverifiedUser = new _User({
        id: uuid(),
        firstName: userDTO.firstName,
        lastName: userDTO.lastName,
        phoneNumber: userDTO.phoneNumber,
        email: userDTO.email,
        password: _userpassword,
        role: userDTO.role,
        verified: false

    })
    // console.log(signUp_User)
    const userData = await unverifiedUser.save()
        .then((result) => {
            return {
                message: "user saved successfully",
                data: result
            }
        }).catch((error) => {
            message: error.message
        })


    const userVerifiedData = Object.values(userData)
    console.log(userVerifiedData);
    // console.log(userData.data);

    // return userData


    const OTPverified = sendOTPVerfication(userData.data)
    console.log(OTPverified)
    return {
        verify: OTPverified,
        data: userData
    }
};

export const verificationService = (data) => {

    verifyOTPservice(data);
}

export const deleteAccountService = async (id) => {

    // delete user record from database
    const deletedUser = await _User.findOneAndDelete({ _id: id })
    // console.log(deletedUser)
    if (deletedUser == null || !deletedUser) {
        return {
            Title: "deleting user Failed ",
            message: "oooops !!!!deletedUser failed "
        }
    }
    return {
        Title: "deleting user  ",
        message: "deleting user success"
    }
    // const deletedUser = await _User.findById(userId, (err, doc) => {

    //     if (!deletedUser) {
    //         return {
    //             message: " something went wrong",
    //             status: 404
    //         }
    //     }
    //     doc.remove(() => {
    //         return {
    //             title: "Delete",
    //             message: "user deleted successful"
    //         }
    //     })
    // })

}

