export const ROLES = {
    ADMIN: 'admin',
    BUYERS: 'buyer',
    SELLERS: 'seller',
    SUPERADMIN: 'superadmin'
}


export const STATE = {
    ACTIVE: 'active',
    PENDING: 'pending',
    NON_ACTIVE: 'non_active',
}
export const gender = ['male', 'female', 'other']

