sourcesContent:
import { sendOTPVerfication, verifyOTPservice } from "./OTP.service"




export const sendOtpController = async (req, res) => {
    const { id, email } = req.body
    const OTPservice = sendOTPVerfication({ id, email })
    if (!OTPservice) {
        return 'something wrong oooops!!!!!!!!!'
    }
    return {
        Title: "verify OTP",
        message: "otp sent successfully"
    }

}

