    service: 'gmail',
    host: "smtp.gmail.com",
    port: 587,
    secure: false, // Use `true` for port 465, `false` for all other ports
    auth: {
        user: EMAIL_ADDRESS,
        pass: EMAIL_PASSWORD,
    },
});

// test the transporter

transporter.verify((err, result) => {
    if (err) {
        console.log(err)

    }
    console.log(result)
    return result
})


